actions.script:
import org.moqui.impl.entity.EntityDefinition.MasterDetail
import org.moqui.impl.entity.EntityDefinition.MasterDefinition

def getAllRelatedEntities(List<MasterDetail> details, Set<String> entityNames = new HashSet<>()) {
for (MasterDetail detail in details) {
    entityNames.add(detail.relInfo.relatedEd.getFullEntityName()) // Add related entity name
    getAllRelatedEntities(detail.internalDetailList, entityNames) // Recursively fetch related entities
}
    return entityNames
}
// Collect all related entities across all masters
Set<String> allRelatedEntities = new HashSet<>()
for (MasterDefinition masterDef in masterDefinitions.values()) {
    allRelatedEntities.addAll(getAllRelatedEntities(masterDef.detailList))
}
// Store related entities in context
relatedEntities.addAll(allRelatedEntities)
